import pytest

# Local
import solution_part2 as solution

@pytest.mark.parametrize(
    "filename,steps,expected",
    [
        pytest.param("puzzle-input-example.txt", 0, "NNCB"),
        pytest.param("puzzle-input-example.txt", 1, "NCNBCHB"),
        pytest.param("puzzle-input-example.txt", 2, "NBCCNBBBCBHCB"),
        pytest.param("puzzle-input-example.txt", 3, "NBBBCNCCNBBNBNBBCHBHHBCHB"),
        pytest.param("puzzle-input-example.txt", 4, "NBBNBNBBCCNBCNCCNBBNBBNBBBNBBNBBCBHCBHHNHCBBCBHCB"),
        pytest.param("puzzle-input-example.txt", 10, ""),
    ],    
)
def test_polymer_propagation_steps(filename, steps, expected):
    assert solution.process_steps(filename, steps) == expected


@pytest.mark.parametrize(
    "filename,steps,expected",
    [
        pytest.param("puzzle-input-example.txt", 10, 1588),
        pytest.param("puzzle-input-example.txt", 25, 64726890),
        pytest.param("puzzle-input-example.txt", 30, 2108829309),
        pytest.param("puzzle-input-example.txt", 40, 2188189693529),
        pytest.param("puzzle-input-full.txt", 40, 2422444761283),                                                   
    ],    
)
def test_part2_solution(filename, steps, expected):
    assert solution.determine_score(filename, steps) == expected    